mapper.select:
select bb.*, (select count(*) from comment_tb where b_idx=bb.b_idx) as comment_count from ( select rank() over(order by b_ref desc, b_step asc) as no, b.* from ( select * from board
b_subject like '%' || #{ subject } || '%'
or b_content like '%' || #{ content } || '%'
or mem_name like '%' || #{ name } || '%'
) b ) bb where no between #{ start } and #{ end }
